// HTML Parser Code - n8n Compatible (No external dependencies)
// Get HTML content from previous node
const htmlContent = $input.first().json.data;

// Helper function to extract text between tags
function extractTextBetweenTags(html, tagName) {
  const regex = new RegExp(`<${tagName}[^>]*>([^<]*)<\/${tagName}>`, 'gi');
  const matches = [];
  let match;
  while ((match = regex.exec(html)) !== null) {
    matches.push(match[1].trim());
  }
  return matches;
}

// Helper function to extract attribute value
function extractAttribute(html, selector, attribute) {
  const regex = new RegExp(`<${selector}[^>]*${attribute}=["']([^"']*)["'][^>]*>`, 'i');
  const match = html.match(regex);
  return match ? match[1] : '';
}

// Helper function to count occurrences of a tag
function countTags(html, tagName) {
  const regex = new RegExp(`<${tagName}[^>]*>`, 'gi');
  const matches = html.match(regex);
  return matches ? matches.length : 0;
}

// Extract title
const titleMatch = htmlContent.match(/<title[^>]*>([^<]*)<\/title>/i);
const title = titleMatch ? titleMatch[1].trim() : '';

// Extract meta description
let description = '';
const metaDescRegex = /<meta[^>]*name=["']description["'][^>]*content=["']([^"']*)["'][^>]*>/i;
const metaDescMatch = htmlContent.match(metaDescRegex);
if (metaDescMatch) {
  description = metaDescMatch[1];
} else {
  // Try og:description
  const ogDescRegex = /<meta[^>]*property=["']og:description["'][^>]*content=["']([^"']*)["'][^>]*>/i;
  const ogDescMatch = htmlContent.match(ogDescRegex);
  if (ogDescMatch) {
    description = ogDescMatch[1];
  }
}

// Extract keywords
const keywordsRegex = /<meta[^>]*name=["']keywords["'][^>]*content=["']([^"']*)["'][^>]*>/i;
const keywordsMatch = htmlContent.match(keywordsRegex);
const keywords = keywordsMatch ? keywordsMatch[1] : '';

// Extract H1 tags
const h1Tags = extractTextBetweenTags(htmlContent, 'h1');

// Extract H2 tags
const h2Tags = extractTextBetweenTags(htmlContent, 'h2');

// Count images
const totalImages = countTags(htmlContent, 'img');

// Count images with alt text
const imgWithAltRegex = /<img[^>]*alt=["'][^"']*["'][^>]*>/gi;
const imagesWithAltMatches = htmlContent.match(imgWithAltRegex);
const imagesWithAlt = imagesWithAltMatches ? imagesWithAltMatches.length : 0;
const imagesWithoutAlt = totalImages - imagesWithAlt;

// Extract canonical URL
const canonicalRegex = /<link[^>]*rel=["']canonical["'][^>]*href=["']([^"']*)["'][^>]*>/i;
const canonicalMatch = htmlContent.match(canonicalRegex);
const canonicalUrl = canonicalMatch ? canonicalMatch[1] : '';

// Check for schema markup
const schemaRegex = /<script[^>]*type=["']application\/ld\+json["'][^>]*>/i;
const hasSchemaMarkup = schemaRegex.test(htmlContent);

// Return structured data
return {
  url: $input.first().json.url || '',
  title: title,
  description: description,
  keywords: keywords,
  h1_tags: h1Tags,
  h2_tags: h2Tags,
  h1_count: h1Tags.length,
  h2_count: h2Tags.length,
  total_images: totalImages,
  images_with_alt: imagesWithAlt,
  images_without_alt: imagesWithoutAlt,
  canonical_url: canonicalUrl,
  has_schema_markup: hasSchemaMarkup,
  content_length: htmlContent.length,
  analyzed_at: new Date().toISOString()
};

// Process and format SEO data for reporting
const inputData = $input.all();

// Debug: Log all input data to see what we're receiving
console.log('=== DEBUG: All Input Data ===');
console.log('Number of input items:', inputData.length);
inputData.forEach((item, index) => {
  console.log(`Input ${index}:`, JSON.stringify(item, null, 2));
});

// Try multiple ways to find the data
let htmlData = null;
let summaryData = null;
let analysisData = null;

// Method 1: Look for HTML parsing data
htmlData = inputData.find(item => 
  item.json && (item.json.title !== undefined || item.json.url !== undefined)
);

// Method 2: Try to get data from specific nodes
try {
  const htmlParserData = $('HTML Parser Code').all();
  console.log('HTML Parser Data:', htmlParserData);
  if (htmlParserData && htmlParserData.length > 0) {
    htmlData = htmlParserData[0];
  }
} catch (error) {
  console.log('Could not get HTML Parser data:', error.message);
}

// Method 3: Look for AI-generated content
summaryData = inputData.find(item => 
  item.json && 
  item.json.message && 
  item.json.message.content && 
  item.json.message.content.length < 200
);

analysisData = inputData.find(item => 
  item.json && 
  item.json.message && 
  item.json.message.content && 
  item.json.message.content.length > 200
);

// Method 4: Try to get AI data from OpenAI nodes
try {
  const aiData = $('OpenAI').all();
  console.log('OpenAI Data:', aiData);
  if (aiData && aiData.length > 0) {
    aiData.forEach((data, index) => {
      const content = data.json?.message?.content || data.json?.choices?.[0]?.message?.content;
      if (content) {
        if (content.length < 200 && !summaryData) {
          summaryData = data;
        } else if (content.length >= 200 && !analysisData) {
          analysisData = data;
        }
      }
    });
  }
} catch (error) {
  console.log('Could not get OpenAI data:', error.message);
}

console.log('=== Found Data ===');
console.log('HTML Data found:', !!htmlData);
console.log('Summary Data found:', !!summaryData);
console.log('Analysis Data found:', !!analysisData);

// Get project name from Edit Fields node
let projectName = 'Unnamed Project';
let websiteUrl = 'No URL provided';

try {
  const editFieldsData = $('Edit Fields').first();
  console.log('Edit Fields Data:', editFieldsData);
  if (editFieldsData && editFieldsData.json) {
    if (editFieldsData.json.project_name) {
      projectName = editFieldsData.json.project_name;
    }
    if (editFieldsData.json.url) {
      websiteUrl = editFieldsData.json.url;
    }
  }
} catch (error) {
  console.log('Could not retrieve data from Edit Fields node:', error.message);
}

// Try to get URL from HTTP Request node if not found in Edit Fields
if (websiteUrl === 'No URL provided') {
  try {
    const httpData = $('HTTP Request').first();
    console.log('HTTP Request Data:', httpData);
    if (httpData && httpData.json && httpData.json.url) {
      websiteUrl = httpData.json.url;
    }
  } catch (error) {
    console.log('Could not retrieve URL from HTTP Request node:', error.message);
  }
}

// Format the processed data
const processedData = {
  // Basic Info
  url: websiteUrl,
  title: htmlData?.json?.title || 'No title found',
  description: htmlData?.json?.description || htmlData?.json?.meta_description || 'No description found',
  keywords: htmlData?.json?.keywords || htmlData?.json?.meta_keywords || 'No keywords found',

  // Technical SEO Metrics
  h1_count: htmlData?.json?.h1_tags?.length || htmlData?.json?.h1_count || 0,
  h1_tags: htmlData?.json?.h1_tags?.join(', ') || htmlData?.json?.h1_tags || 'None',
  h2_count: htmlData?.json?.h2_tags?.length || htmlData?.json?.h2_count || 0,
  h2_tags: htmlData?.json?.h2_tags?.join(', ') || htmlData?.json?.h2_tags || 'None',
  total_images: htmlData?.json?.total_images || htmlData?.json?.image_count || 0,
  images_without_alt: htmlData?.json?.images_without_alt || htmlData?.json?.images_no_alt || 0,
  images_with_alt: (htmlData?.json?.total_images || htmlData?.json?.image_count || 0) - (htmlData?.json?.images_without_alt || htmlData?.json?.images_no_alt || 0),
  has_schema_markup: htmlData?.json?.has_schema_markup || htmlData?.json?.schema_markup || false,
  canonical_url: htmlData?.json?.canonical_url || htmlData?.json?.canonical || 'Not found',

  // Content Analysis
  content_length: htmlData?.json?.content_length || 0,
  word_count: htmlData?.json?.word_count || 0,

  // AI Generated Content - handle different OpenAI response formats
  summary: summaryData?.json?.message?.content || 
           summaryData?.json?.choices?.[0]?.message?.content || 
           summaryData?.json?.content ||
           'Summary generation failed',
  detailed_analysis: analysisData?.json?.message?.content || 
                    analysisData?.json?.choices?.[0]?.message?.content || 
                    analysisData?.json?.content ||
                    'Analysis generation failed',

  // Metadata
  analyzed_at: new Date().toISOString(),
  analysis_date: new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  }),
  analysis_time: new Date().toLocaleTimeString('en-US', {
    hour: '2-digit',
    minute: '2-digit'
  }),
  project_name: projectName
};

// Calculate SEO Score based on various factors
let seoScore = 0;
const scoringCriteria = [];

// Title tag (20 points)
if (processedData.title && processedData.title !== 'No title found') {
  seoScore += 20;
  scoringCriteria.push('‚úì Title tag present');
} else {
  scoringCriteria.push('‚úó Missing title tag');
}

// Meta description (20 points)
if (processedData.description && processedData.description !== 'No description found') {
  seoScore += 20;
  scoringCriteria.push('‚úì Meta description present');
} else {
  scoringCriteria.push('‚úó Missing meta description');
}

// Keywords (15 points)
if (processedData.keywords && processedData.keywords !== 'No keywords found') {
  seoScore += 15;
  scoringCriteria.push('‚úì Keywords found');
} else {
  scoringCriteria.push('‚úó No keywords found');
}

// H1 tags (15 points)
if (processedData.h1_count > 0) {
  seoScore += 15;
  scoringCriteria.push(`‚úì H1 tags present (${processedData.h1_count})`);
} else {
  scoringCriteria.push('‚úó No H1 tags found');
}

// Image optimization (15 points)
if (processedData.total_images > 0) {
  if (processedData.images_without_alt === 0) {
    seoScore += 15;
    scoringCriteria.push('‚úì All images have alt text');
  } else {
    const altPercentage = Math.round(((processedData.total_images - processedData.images_without_alt) / processedData.total_images) * 100);
    seoScore += Math.round(15 * (altPercentage / 100));
    scoringCriteria.push(`‚ö† ${altPercentage}% of images have alt text`);
  }
} else {
  scoringCriteria.push('- No images to analyze');
}

// Schema markup (15 points)
if (processedData.has_schema_markup) {
  seoScore += 15;
  scoringCriteria.push('‚úì Schema markup detected');
} else {
  scoringCriteria.push('‚úó No schema markup found');
}

// Add calculated fields
processedData.seo_score = Math.min(seoScore, 100); // Cap at 100
processedData.seo_grade = seoScore >= 80 ? 'A' : 
                         seoScore >= 60 ? 'B' : 
                         seoScore >= 40 ? 'C' : 'D';

processedData.seo_status = seoScore >= 80 ? 'Excellent' :
                          seoScore >= 60 ? 'Good' :
                          seoScore >= 40 ? 'Needs Improvement' : 'Poor';

processedData.scoring_criteria = scoringCriteria;

// Generate recommendations based on score
const recommendations = [];
if (processedData.title === 'No title found') {
  recommendations.push('Add a descriptive title tag (50-60 characters)');
}
if (processedData.description === 'No description found') {
  recommendations.push('Add a compelling meta description (150-160 characters)');
}
if (processedData.h1_count === 0) {
  recommendations.push('Add at least one H1 tag to structure your content');
}
if (processedData.images_without_alt > 0) {
  recommendations.push(`Add alt text to ${processedData.images_without_alt} images for better accessibility`);
}
if (!processedData.has_schema_markup) {
  recommendations.push('Consider adding schema markup for better search visibility');
}
if (processedData.keywords === 'No keywords found') {
  recommendations.push('Add relevant keywords to improve search targeting');
}

processedData.recommendations = recommendations;

// Log the processed data for debugging
console.log('Processed SEO Data:', JSON.stringify(processedData, null, 2));

return processedData;

